mapper.insert:

	 LAST_INSERT_ID()

insert into tb_category (
	cate_name,   pcate_id,   use_yn
) values (
	#{cateName}, #{pcateId}, 'Y'
)

mapper.select:
select P.cate_id as cateId , P.cate_name as cateName , P.pcate_id as pcateId , P.use_yn as useYn , C.tag as tag from tb_category P left join tb_category_tag C
P.cate_id = #{pcateId}
and C.tag like concat('%', #{tag}, '%')

mapper.insert:

	 LAST_INSERT_ID()

insert into tb_category_tag (
	cate_id,   tag
) values (
	#{cateId}, #{tag}
)


	 LAST_INSERT_ID()

