mapper.select:
select 
from tb_product

	
		mall_id = #{mallId}
	
	
		AND cate_id = #{cateId}

select 
from tb_product

	use_yn = #{useYn}
	and mall_id = #{mallId}
	and prd_name like concat('%', #{prdName}, '%')

order by prd_seq desc 
limit #{startRow}, #{pageSize}

select count(*)
from tb_product

	use_yn = #{useYn}
	and mall_id = #{mallId}
	and prd_name like concat('%', #{prdName}, '%')

mapper.insert:

	insert into tb_product (
			prd_type
		,	cate_id
		,	mall_id
		,	prd_name
		,	prd_org_id
		,	prd_url
		,	prd_price
		,	prd_thumb_url
		,	use_yn
	) values (
			#{prdType}
		,	#{cateId}
		,	#{mallId}
		,	#{prdName}
		,	#{prdOrgId}
		,	#{prdUrl}
		,	#{prdPrice}
		,	#{prdThumbUrl}
		,	#{useYn}
	)

mapper.update:
update tb_product set
cate_id = #{cateId}
use_yn = #{useYn}
where prd_seq = #{prdSeq}

mapper.delete:
delete from tb_product

	
		mall_id = #{mallId}
	
	
		and prd_type = #{prdType}
